mapper.select:
SELECT ROWNUM NUM, B.*, M.EMAIL, M.USER_NAME FROM BOARD_DATA B
LEFT JOIN MEMBER M ON M.USER_NO = B.MEMBER_SEQ
WHERE B.SEQ = #{seq}

SELECT * FROM (SELECT ROWNUM NUM, D.* FROM (
SELECT B.*, M.EMAIL, M.USER_NAME FROM BOARD_DATA B
LEFT JOIN MEMBER M ON M.USER_NO = B.MEMBER_SEQ


        AND B.BID = #{bId}


        AND B.CATEGORY = #{category}





            AND B.SUBJECT || B.CONTENT || B.POSTER || M.EMAIL || M.USER_NAME
            LIKE #{keyword}


            AND B.SUBJECT || B.CONTENT LIKE #{keyword}


            AND B.SUBJECT LIKE #{keyword}


            AND B.CONTENT LIKE #{keyword}


            AND B.POSTER || M.EMAIL || M.USER_NAME LIKE #{keyword}



ORDER BY B.SEQ DESC
) D)
WHERE

NUM >= #{offset} AND NUM < #{endRows}

SELECT COUNT(*) FROM BOARD_DATA B
LEFT JOIN MEMBER M ON B.MEMBER_SEQ = M.USER_NO


        AND B.BID = #{bId}


        AND B.CATEGORY = #{category}





            AND B.SUBJECT || B.CONTENT || B.POSTER || M.EMAIL || M.USER_NAME
            LIKE #{keyword}


            AND B.SUBJECT || B.CONTENT LIKE #{keyword}


            AND B.SUBJECT LIKE #{keyword}


            AND B.CONTENT LIKE #{keyword}


            AND B.POSTER || M.EMAIL || M.USER_NAME LIKE #{keyword}

SELECT * FROM (SELECT ROWNUM NUM, D.* FROM (
SELECT B.*, M.EMAIL, M.USER_NAME FROM BOARD_DATA B
LEFT JOIN MEMBER M ON M.USER_NO = B.MEMBER_SEQ


        AND B.CATEGORY = #{category}




            AND (B.SUBJECT || B.CONTENT || B.POSTER || M.EMAIL || M.USER_NAME LIKE #{keyword})


            AND (B.SUBJECT || B.CONTENT LIKE #{keyword})


            AND B.SUBJECT LIKE #{keyword}


            AND B.CONTENT LIKE #{keyword}


            AND (B.POSTER || M.EMAIL || M.USER_NAME LIKE #{keyword})



ORDER BY B.SEQ DESC
) D)
WHERE

NUM >= #{offset} AND NUM < #{endRows}
